// Helper function to safely get node data
const getNodeData = (nodeName) => {
	try {
		return eval(`$('${nodeName}').first()`);
	} catch (e) {
		return null;
	}
};

const output = [];

// Get the input text for the chat
const chatInput = $input.first().json.text;

// Default to the webhook id, since it is almost always present
let sessionId = null;
const webhookData = getNodeData("message_webhook");
if (webhookData && webhookData.json && webhookData.json.body && webhookData.json.body.chat_id) {
	sessionId = webhookData.json.body.chat_id;
}

// Optionally override with the message_received sessionId, if available
const messageData = getNodeData("message_received");
if (messageData && messageData.json && messageData.json.sessionId) {
	sessionId = messageData.json.sessionId;
}

// Return an array of objects with both the chat input and the session id
output.push({ json: { chatInput, sessionId } });
return output;

const chunkSize = 15000;
const chunkOverlap = 1000;
const text = $input.item.json.data.replace(/\n/, '');

const chunks = [];
let remainingText = text;

while (remainingText.length > 0) {
    let splitPoint;

    // Try splitting at paragraph level first
    splitPoint = remainingText.lastIndexOf("\n\n", chunkSize);

    // If no paragraph split, try splitting at sentence level
    if (splitPoint === -1) {
        splitPoint = remainingText.lastIndexOf(". ", chunkSize);
    }

    // If no sentence split, try splitting at word level
    if (splitPoint === -1) {
        splitPoint = remainingText.lastIndexOf(" ", chunkSize);
    }

    // If still no split point, force cut at chunkSize
    if (splitPoint === -1 || splitPoint < chunkSize * 0.5) {  
        splitPoint = chunkSize; // Hard split if no good split point
    }

    // Extract chunk and adjust remaining text with overlap
    let chunk = remainingText.substring(0, splitPoint).trim();
    chunks.push(chunk);

    // Move the pointer forward while keeping the overlap
    remainingText = remainingText.substring(Math.max(0, splitPoint - chunkOverlap)).trim();

    // Break if remaining text is too small to form another chunk
    if (remainingText.length < chunkSize * 0.2) {
        chunks.push(remainingText);
        break;
    }
}

return { chunks };

const chunkSize = 3250;
const chunkOverlap = 1000;

// Get the text from the previous node.
let text = $('Split Chunks').item.json.chunk_large;

// Ensure text is a string. If it's an array, join its items.
if (Array.isArray(text)) {
    text = text.join(" ");
} else if (typeof text !== 'string') {
    text = String(text);
}

// Replace single newlines with spaces but keep double newlines intact for paragraph splits.
text = text.replace(/\n\n/g, '<<<PARA>>>')  // Temporarily mark paragraphs
           .replace(/\n/g, ' ')             // Replace remaining single newlines with spaces
           .replace(/<<<PARA>>>/g, '\n\n');  // Restore paragraph breaks

const chunks = [];
let remainingText = text;

while (remainingText.length > 0) {
    let splitPoint;

    // Try splitting at paragraph level first
    splitPoint = remainingText.lastIndexOf("\n\n", chunkSize);

    // If no paragraph break is found, try splitting at sentence level
    if (splitPoint === -1) {
        splitPoint = remainingText.lastIndexOf(". ", chunkSize);
    }

    // If no sentence boundary is found, try splitting at word level
    if (splitPoint === -1) {
        splitPoint = remainingText.lastIndexOf(" ", chunkSize);
    }

    // If still no split point or the split is too early, force a hard split at chunkSize
    if (splitPoint === -1 || splitPoint < chunkSize * 0.5) {  
        splitPoint = chunkSize;
    }

    // Extract the chunk and add it to the array (trim to remove extra whitespace)
    const chunk = remainingText.substring(0, splitPoint).trim();
    chunks.push(chunk);

    // Advance the pointer while keeping an overlap for context
    remainingText = remainingText.substring(Math.max(0, splitPoint - chunkOverlap)).trim();

    // If the remaining text is too short, add it as the final chunk (if not empty) and exit the loop
    if (remainingText.length < chunkSize * 0.2) {
        if (remainingText.length > 0) {
            chunks.push(remainingText);
        }
        break;
    }
